window.onload=function(){let btn=document.querySelectorAll("button"),value=document.getElementsByTagName("input"),answer=document.getElementsByClassName("answer");value[0].addEventListener("oninput",changeHandler=e=>{const value=e.value;e.value=value.replace(/\D/g,"")}),btn.forEach((function(btn){btn.addEventListener("click",(function(e){""==value[0].value?answer[0].textContent="Please, enter your number first":value[0].value%2==0?(document.body.style.background="#69f0ae",document.body.style.transition=".5s ease-in-out",answer[0].textContent="Your number is even!",value[0].value=""):(document.body.style.background="#ff8a80",document.body.style.transition=".5s ease-in-out",answer[0].textContent="Your number is odd!",value[0].value="")}))}))};